import React, { Component } from 'react';
import { Card } from 'semantic-ui-react';
import container from '../ethereum/container';


class ReviewIndex extends Component {

    static async getInitialProps(){
        const info = await container.methods.GetUserInfo().call();

        return {
            ContainerNo: info[0],
            WarehouseNo: info[1],
            ProductName: info[2],
            HalalStatus: info[3],
            Quantity: info[4]
        };
    }

    renderInfo() {

        <table class="ui celled table">
            <thead>
            <tr><th>Container ID</th>
            <th>Warehouse ID</th>
            <th>Product Name</th>
            <th>Halal Status</th>
            <th>Quantity</th>
            </tr></thead>
        <tbody>
        <tr>
            <td data-label="Container ID" ContainerNo></td>
            <td data-label="Warehouse ID" WarehouseNo></td>
            <td data-label="Product Name" ProductName></td>
            <td data-label="Halal Status" HalalStatus></td>
            <td data-label="Quantity" Quantity></td>
        </tr>
        
    </tbody>
    </table>
    }

    render(){
        return <div>{this.renderInfo()}</div>;
    }
}

export default ReviewIndex;
