import React, { Component } from 'react';
import { Card } from 'semantic-ui-react';
import container from '../ethereum/container';


class ReviewIndex extends Component {

    static async getInitialProps(){
        const info = await container.methods.GetUserInfo().call();

        return {
            ContainerNo: info[0],
            WarehouseNo: info[1],
            ProductName: info[2],
            HalalStatus: info[3],
            Quantity: info[4]
        };
    }

    renderInfo() {
        const{
            ContainerNo,
            WarehouseNo,
            ProductName,
            HalalStatus,
            Quantity
        } = this.props;

        const items = [
            {
                header: ContainerNo,
                meta:'Container ID',
                description:'View details',
                fluid: true,
                style:{ overflowWrap:'break-word'}
            }

        ];
        return<Card.Group items={items}/>
    }

    render(){
        return (<div>{this.renderInfo()}</div>);
    }

}
export default ReviewIndex;
