import React, { Component, Menu, useEffect } from 'react';
import { Button, Table, Dropdown, Form, Select, Grid , Cell, Item} from 'semantic-ui-react';
import Input from '../../ethereum/container';
import RequestShow from '../../components/RequestShow';
import Layout from '../../components/Layout';
import DataTable from 'react-data-table-component';





var temp

var data= [
    {cID:'', HalStatus:'', Booked:'', SDate:'', EDate:'', Product:'', Quantity:''}
]

var count = 0;

const option2 = [
    { key: 'AB', text: 'Available', value: 'Available' },
    { key: 'NAB', text: 'Non-Available', value: 'Non-Available' },
    { key: 'D', text: 'Destroy', value: 'Destroy' }
]

const columns = [
    {
        name:'ID',
        selector:(row) => row.cID,
    },

    {
        name:'Halal',
        selector:(row) => row.HalStatus,
    },

    {
        name:'Status',
        selector:(row) => row.Booked,
    },

    {
        name:'Start Date',
        selector:(row) => row.SDate,
    },


];

class showCont extends Component{

    static async getInitialProps(){
     
        const contCount = await Input.methods.getContainerCount().call();

        const container = await Promise.all(
            Array(parseInt(contCount)).fill().map((element, index) => {
                return Input.methods.container(index).call()
            })
        );
        return {container, contCount};

    }


    // renderShow(){
    //     return data.map((cont, index) => {
    //         return (
    //             <RequestShow
    //                 id={index}
    //                 request={cont}
    //             />
    //         );
    //     });
    // }

    onChangedStatus (event){
        temp =  []
        for (var i = 0; i < data.length; i++){
            if (data[i][0].Booked == event.target.value){
                temp.push(data[i])
            };
        }   
        this.renderShow();
    }

    //   filterData(event){
    //     temp =  []
    //     for (var i = 0; i < data.length; i++){
    //         if (data[i][0].Booked == event.target.value){
    //             temp.push(data[i])
    //         };
    //     } 
        
    //     return this.props.container.map((temp, index) => {
    //         return (
    //             <RequestShow
    //                 id={index}
    //                 data={temp}
    //             />
    //         );
    //     });       
    //   }
      
      renderShow(){
        
        temp =  []
        for (var i = 0; i < data.length; i++){
            if (data[i][0].HalStatus == 'Halal'){
                temp.push(data[i])
            };
        }  
        // return temp.map((temp, index) => {
        //     return (
        //         <RequestShow
        //             id={index}
        //             cID={temp[0].cID}
        //             HalStatus={temp[0].HalStatus}
        //             Booked={temp[0].Booked}
        //             SDate={temp[0].SDate}
        //             EDate={temp[0].EDate}
        //             Product={temp[0].Product}
        //             Quantity={temp[0].Quantity}
        //         />
        //     );
        // });
        
        
        console.log(temp)
        console.log('xxx')
        console.log(data)
        return temp.map((temp, index) => {
            return (
                
                <Item>
                      <Item.Content>
                        <div class = "right floated">
                        <div class = "ui big right ribbon label red">{temp[0].cID}</div>
                        </div>
                        <Item.Meta>
                        <h2><h4 style = {{color: 'teal'}}>Identity Number:</h4>{temp[0].Product}</h2>
                        </Item.Meta>
                        <Item.Description>
                              <h2><p><h4>Full Name:</h4>{temp[0].HalStatus}</p></h2>
                              <h2><p><h4>Citizenship:</h4>{temp[0].Booked}</p></h2>
                              
                              <h2><p><h4>Address:</h4>{temp[0].SDate}</p></h2>
                        </Item.Description>

                        <Item.Extra>

                        </Item.Extra>
                      </Item.Content>
                    </Item>
               
            );
        });
      }

      getContainerInfo(){
         return this.props.container.find((request, index) => {
            {this.renderTable(request, index)}
         });
    
     }

    renderTable(request, i){

        data[i] = [
            {cID:request._ContainerNo, HalStatus:request._HalalStatus, Booked:request._booked, SDate:request._StartDate, EDate:request._EndDate, Product:request._ProductName, Quantity:request._Quantity}
        ]
        temp = data
    }

    render(){

        const { Header, Row, HeaderCell, Body} = Table;

        return(
            <div style={{ backgroundImage:"url('https://images.pexels.com/photos/1095814/pexels-photo-1095814.jpeg')", 
            height:'100vh',
            backgroundSize: 'cover',
            backgroundRepeat: 'no-repeat'}}>
        <Layout>

            <link rel="stylesheet" href="//cdnjs.cloudflare.com/ajax/libs/semantic-ui/2.2.12/semantic.min.css"/>
            <span> </span>
            <h3 align="center" style={{color:'#ffffff', fontWeight:'bold'}} >Container Details</h3>
            <div style={{marginLeft:'80px', marginRight:'80px'}}>
            <div style={{color:'#ffffff', fontWeight:'bold', width:'200px'}}>
                <Form>
                        <label style={{color:'#ffffff', fontWeight:'bold'}}> Status </label>
                        <Form.Field
                        control={Select}
                        options={option2}
                        onChange={this.onChangedStatus}/>
                        </Form>
             </div>
                    
            <Table>
                <Header>
                    <Row>
                        <HeaderCell>ID</HeaderCell>
                        <HeaderCell>Container ID</HeaderCell>
                        <HeaderCell>Halal status</HeaderCell>
                        <HeaderCell>Status</HeaderCell>
                        <HeaderCell>Start Date</HeaderCell>
                        <HeaderCell>End Date</HeaderCell>
                        <HeaderCell>Product Name</HeaderCell>
                        <HeaderCell>Quantity</HeaderCell>
                    </Row>
                </Header>
                <Body>{this.getContainerInfo()}{this.renderShow()}</Body>
            </Table>
            </div>
            <br></br>
            <DataTable
            columns={columns}
            t={temp}></DataTable>
            </Layout>
            </div>
            
        );
    }
}

export default showCont;
