import React, { Component } from 'react';
import { Form, Button } from 'semantic-ui-react';
import Layout from '../../components/Layout';
import container from '../../ethereum/container';
import web3 from '../../ethereum/web3';

class FormCreate extends Component {

    state = {
        ContainerID:'',
        WarehouseID:'',
        ProductName:'',
        HalalSatus:'',
        Quantity:''

    }


    onSubmit = async (event) => {
        event.preventDefault();

        const accounts = await web3.eth.getAccounts();

        await container.methods.SetUserInfo(this.state.ContainerID.WarehouseID.ProductName.HalalSatus.Quantity).send({from: accounts [0]});
    };

    render() {
        return (

            <div>
                <h3> Container Form</h3>

                <Form onSubmit={this.onSubmit}>
                    <Form.Field>
                        <label>Container ID</label>
                        <input 
                            value={this.state.ContainerID}
                            onChange={event =>
                                this.setState({ContainerID: event.target.value})}
                        />
                        <label>Warehouse ID</label>
                        <input 
                            value={this.state.WarehouseID}
                            onChange={event =>
                                this.setState({WarehouseID: event.target.value})}
                        />
                        <label>Product Name</label>
                        <input 
                            value={this.state.ProductName}
                            onChange={event =>
                                this.setState({ProductName: event.target.value})}
                        />
                        <label>Halal Status</label>
                        <input
                            label="Halal/Haram"
                            labelPosition="right" 
                            value={this.state.HalalSatus}
                            onChange={event =>
                                this.setState({halalSatus: event.target.value})}
                        />
                        <label>Quantity</label>
                        <input 
                            value={this.state.quantity}
                            onChange={event =>
                                this.setState({quantity: event.target.value})}
                        />
                    </Form.Field>

                    <Button primary>Submit</Button>
                </Form>
            </div>
        );
    }
}

export default FormCreate;
