import React from 'react';
import { Menu } from 'semantic-ui-react';
import { Link } from '../routes';


export default () => {
    return (
        <div style={{backgroundColor:'lightblue' }}>
            
            <Menu style={{ marginTop: '0px ', marginLeft:'80px', marginRight:'80px'}}>

            <Link route="/">
                <a className="item">Halal Logistic System</a>
            </Link>

            <Menu.Menu position="right">

                <Link route="/form/Record">
                    <a className="item">Records</a>
                </Link>

            </Menu.Menu>          
            </Menu>
        </div>

    );
};
