import React, { Component, Menu } from 'react';
import { Button, Table, Dropdown, Form, Select, Grid , Cell} from 'semantic-ui-react';
import Input from '../../ethereum/container';
import RequestCont from '../../components/RequestCont';
import Layout from '../../components/Layout';
import container from '../ethereum/container';
import web3 from '../ethereum/web3';


var temp
var data= [
    {ID:'', HalStatus:'', Booked:'', SDate:'', EDate:'', Product:'', Quantity:''}
]

class RequestShow extends Component{

    getContainerInfo(){
        return this.props.container.find((request, index) => {
        for (var i = 0; i < this.props.contCount; i++){
                {this.renderTable(request)}
            }
        });
    }

   renderTable(request){
       data= [
           {ID:request._ContainerNo, HalStatus:request._HalalStatus, Booked:request._booked, SDate:request._StartDate, EDate:request._EndDate, Product:request._ProductName, Quantity:request._Quantity}
       ]
       temp = data;
   }

    render(){
        const {Row, Cell} = Table;
        const {cont, id} = this.props;

        return(
            data.map((cont, i) => {
            <Row>
                 <Row>
                        <Cell>{id}</Cell>
                        <Cell>{cont.ID}</Cell>
                        <Cell>{cont.HalStatus}</Cell>
                        <Cell>{cont.Booked}</Cell>
                        <Cell>{cont.SDate}</Cell>
                        <Cell>{cont.EDate}</Cell>
                        <Cell>{cont.Product}</Cell>
                        <Cell>{cont.Quantity}</Cell>
                    </Row>
            </Row>
            }))
    }
}

export default RequestShow;
