import React, { Component } from 'react';
import { Form, Button, Message, Input , Select, FormField} from 'semantic-ui-react';
import Layout from '../../components/Layout';
import container from '../../ethereum/container';
import web3 from '../../ethereum/web3';

const options = [
    { key: 'H', text: 'Halal', value: 'Halal' },
    { key: 'NH', text: 'Non-Halal', value: 'Non-Halal' },
]

class defContainerForm extends Component {

    state = {
        ContainerNo:'',
        HalalStatus:'',
        Description:'',
        Supplier:'',
        Size:'',
        errorMessage:'',
        loading:false
    }

    handleHalalStatus = (e, { value }) => this.setState({ HalalStatus: value })

    onSubmit = async (event) => {
        event.preventDefault();

        const { ContainerNo, HalalStatus, Description, Supplier, Size } = this.state;

        this.setState({ loading: true, errorMessage: '' });

    try{
        const accounts = await web3.eth.getAccounts();

        await container.methods.defineContainer(ContainerNo, HalalStatus, Description,this.state.Supplier,this.state.Size).send({from: accounts [0]});

    } catch (err) {
        this.setState({ errorMessage: err.message });
     }

     this.setState({ loading: false });
    };

    render() {
        return (
        <html>        
        <Layout>
            <link rel="stylesheet" href="//cdnjs.cloudflare.com/ajax/libs/semantic-ui/2.2.12/semantic.min.css"/>
            
            <body style={{backgroundColor:'#91d0fa'}}>
            <div class="ui container">
                <div class="equal width fields">
                <h2 align="center">New Container Form</h2>
                <Form onSubmit={this.onSubmit} error={!!this.state.errorMessage}>
                    <Form.Field>
                        <label>Container ID</label>
                        <Input 
                            value={this.state.ContainerNo}
                            onChange={event =>
                                this.setState({ContainerNo: event.target.value})}
                        />
                    </Form.Field>
                        <br/>
                    <Form.Field>
                        label='Halal Status'
                        control={Select}
                        options={options}
                        onChange={this.handleHalalStatus}
                        <br/>
                    </Form.Field>
                    <Form.Field>
                        <label>Description</label>
                        <Input
                            value={this.state.Description}
                            onChange={event =>
                                this.setState({Description: event.target.value})}
                        />
                        <br/>
                    </Form.Field>
                    <Form.Field>
                        <label>Container's Supplier</label>
                        <Input 
                            value={this.state.Supplier}
                            onChange={event =>
                                this.setState({Supplier: event.target.value})}
                        />
                        <br/>
                    </Form.Field>
                    <Form.Field>
                        <label>Size</label>
                        <Input 
                            value={this.state.Size}
                            onChange={event =>
                                this.setState({Size: event.target.value})}
                        />
                    </Form.Field>

                    <Message error header="Oops!" content={this.state.errorMessage}/>
                    <Button loading= {this.state.loading} primary>Submit</Button>
                </Form>
                </div>
                </div>
            </body>
        </Layout>
        </html>
        );
    }
}

export default defContainerForm;
