import { Router, Link } from '../rout';
import React, { Component } from 'react';
import { Card, Form, Button, Table } from 'semantic-ui-react';
import container from '../../ethereum/container';
import Layout from '../../components/Layout';


class RecordShow extends Component {

    state = {
        search:''
    };

    static async getInitialProps(){
        const info = await container.methods.getContainers().call();

        return {
            info
        };
    }
 
renderInfo() {
       const items = this.props.info.map(index => {
        return{
            header: index,
            description: (
                <Link route={`/record/${index}`}>
                    <a>View Record</a>
                </Link>
            ),
            fluid: true
        }
       });
       return <Card.Group items={items}/>;
    }

    onSearch = async event => {
        event.preventDefault();

        Router.pushRoute(`/record/${this.state.search}`);
    }

    render(){
        return (
            <Layout>
                <div>
                   
                    <Form onSubmit={this.onSearch}> 
                        <Form.Field>
                            <Input 
                                fluid 
                                action={{ icon: 'search' }} 
                                placeholder='Search...' 
                                onChange={(event) => this.setState({ search: event.target.value })}
                            /><br/>
                        </Form.Field>
                    </Form>
                     <h2>Records List</h2>
                    {this.renderInfo()}
                </div>
            </Layout>
        );
    }
}

export default RecordShow;
