import React, { Component } from 'react';
import { Form, Button } from 'semantic-ui-react';
import Layout from '../../components/Layout';
import cf from '../../ethereum/cf';
import web3 from '../../ethereum/web3';

class ContainerForm extends Component {

    state = {
        ContainerAddr:'',
        ContainerID:'',
        HalalStatus:'',
        WarehouseNo:'',
        ProductName:'',
        StartDate:'',
        EndDate:'',
        approved:'',
        Quantity:'',
    }


    onSubmit = async (event) => {
        event.preventDefault();

        const accounts = await web3.eth.getAccounts();

        await cf.methods.setContainerInfo(this.state.ContainerID, this.state.HalalStatus, this.state.WarehouseNo,this.state.ProductName,this.state.StartDate, this.state.EndDate, this.state.approved, this).send({from: accounts [0]});

    };

    render() {
        return (
        <html>        
        <Layout>
            <link rel="stylesheet" href="//cdnjs.cloudflare.com/ajax/libs/semantic-ui/2.2.12/semantic.min.css"/>
            
            <body>
            <div class="ui container">
                <div class="equal width fields">
                <h2 align="center">Container Form</h2>
                <Form onSubmit={this.onSubmit}>
                    <Form.Field>
                        <label>Container ID</label>
                        <input 
                            value={this.state.ContainerID}
                            onChange={event =>
                                this.setState({ContainerID: event.target.value})}
                        />
                        <label>Container Size</label>
                        <input 
                            label="In square fit"
                            labelPosition="right"
                            value={this.state.ContainerSize}
                            onChange={event =>
                                this.setState({ContainerSize: event.target.value})}
                        />
                        <label>Current Halal Status</label>
                        <input
                            value={this.state.CurrentHalalStatus}
                            onChange={event =>
                                this.setState({CurrentHalalStatus: event.target.value})}
                        />
                        <label>Supplier of container</label>
                        <input 
                            value={this.state.ContainerBrand}
                            onChange={event =>
                                this.setState({ContainerBrand: event.target.value})}
                        />
                    </Form.Field>

                    <Button primary>Submit</Button>
                </Form>
                </div>
                </div>
            </body>
        </Layout>
        </html>
        );
    }
}

export default ContainerForm;
