import React, { Component, Menu } from 'react';
import { Button, Table, Dropdown } from 'semantic-ui-react';
import Input from '../../ethereum/container';
import RequestCont from '../../components/RequestCont';
import Layout from '../../components/Layout';

class showCont extends Component{


    static async getInitialProps(){
     
        const contCount = await Input.methods.getContainerCount().call();

        const container = await Promise.all(
            Array(parseInt(contCount)).fill().map((element, index) => {
                return Input.methods.container(index).call()
            })
        );

        return {container};

    }

    renderRowsCont(){
        return this.props.container.map((request, index) => {
            return (
                <RequestCont
                    id={index}
                    request={request}
                />
            );
        });
    }

    onClickedStatus = async event => {
        event.preventDefault();
        
        return this.props.container.some((request, index) => {
            for (var i = 0; i < this.props.contCount; i++){
                 if(event.target.value ===  request._ContainerNo){
                     this.setState({_StartDate:request._StartDate, _EndDate:request._EndDate, _ProductName:request._ProductName, _HalalStatus:request._HalalStatus,  _Quantity:request._Quantity})
                     match = true;
                    this.state.value =' ';
                    return true;
                  } else {
                   match = false;
                    this.state.value = 'Please enter a valid Container ID';
                    return false;
                 }
                }
            });
      }

    render(){

        const { Header, Row, HeaderCell, Body} = Table;

        return(
            <div style={{ backgroundImage:"url('https://images.pexels.com/photos/1095814/pexels-photo-1095814.jpeg')", 
            height:'100vh',
            backgroundSize: 'cover',
            backgroundRepeat: 'no-repeat'}}>
        <Layout>

            <link rel="stylesheet" href="//cdnjs.cloudflare.com/ajax/libs/semantic-ui/2.2.12/semantic.min.css"/>
            <span> </span>
            <h3 align="center" style={{color:'#ffffff', fontWeight:'bold'}} >Container Details</h3>
            <div style={{marginLeft:'80px', marginRight:'80px', fontSize:'16px'}}>
            <div style={{color:'white', fontWeight:'bold'}}>
            <Dropdown item text = 'Status'>
                <Dropdown.Menu>
                    <Dropdown.Item>Available</Dropdown.Item>
                    <Dropdown.Item>Not Available</Dropdown.Item>
                    <Dropdown.Item>Destroy</Dropdown.Item>
                </Dropdown.Menu>
            </Dropdown>
            </div>

            <Table>
                <Header>
                    <Row>
                        <HeaderCell>ID</HeaderCell>
                        <HeaderCell>Container ID</HeaderCell>
                        <HeaderCell>Halal status</HeaderCell>
                        <HeaderCell>Status</HeaderCell>
                        <HeaderCell>Start Date</HeaderCell>
                        <HeaderCell>End Date</HeaderCell>
                        <HeaderCell>Product Name</HeaderCell>
                        <HeaderCell>Quantity</HeaderCell>
                    </Row>
                </Header>
                <Body>{this.renderRowsCont()}</Body>
            </Table>
            </div>
            </Layout>
            </div>
        );
    }
}

export default showCont;
