import React, { Component, Menu } from 'react';
import DataTable from 'react-data-table-component';
import { Button, Table, Dropdown, Form, Select, Grid , Cell} from 'semantic-ui-react';
import Input from '../../ethereum/container';
import RequestShow from '../../components/RequestShow';
// import RequestShow from '../../components/RequestShow';
import Layout from '../../components/Layout';


var temp
var data= [
    {cID:'', HalStatus:'', Booked:'', SDate:'', EDate:'', Product:'', Quantity:''}
]

const columns = [
    {
        name:'Container ID',
        selector: (row) => row.cID,
    },
    {
        name:'Halal Status',
        selector: (row) => row.HalStatus,
    },
    {
        name:'Status',
        selector: (row) => row.Booked,
    },
    {
        name:'Start Date',
        selector: (row) => row.SDate,
    },
    {
        name:'End Date',
        selector: (row) => row.EDate,
    },
    {
        name:'Product Name',
        selector: (row) => row.Product,
    },
    {
        name:'Quantity',
        selector: (row) => row.Quantity,
    }
]
const option2 = [
    { key: 'AB', text: 'Available', value: 'Available' },
    { key: 'NAB', text: 'Non-Available', value: 'Non-Available' },
    { key: 'D', text: 'Destroy', value: 'Destroy' }
]

class showCont extends Component{

    static async getInitialProps(){
     
        const contCount = await Input.methods.getContainerCount().call();

        const container = await Promise.all(
            Array(parseInt(contCount)).fill().map((element, index) => {
                return Input.methods.container(index).call()
            })
        );

        return {container, contCount};

    }

    renderRowsCont(){
        {this.getContainerInfo()}
        console.log(this.props.contc)
        for (var i = 0; i < this.props.contCount;i++){
            return (
                <RequestShow
                    id={i}
                    data={data}
                />
            );
        }
    }

    renderComponent(){
        for (var i = 0;i<this.props.contCount;i++){
            {this.getContainerInfo()}
        }
    }
    // renderShow(){
    //     return data.map((cont, index) => {
    //         return (
    //             <RequestShow
    //                 id={index}
    //                 request={cont}
    //             />
    //         );
    //     });
    // }

    onClickedStatus (event){

         temp = data.filter(temp1 => temp1.Booked === event.target.value)

      }

      getContainerInfo(){
         return this.props.container.find((request, index) => {
            {this.renderTable(request)}
         });
    
     }

    renderTable(request){

        data = [
            {cID:request._ContainerNo, HalStatus:request._HalalStatus, Booked:request._booked, SDate:request._StartDate, EDate:request._EndDate, Product:request._ProductName, Quantity:request._Quantity}
        ]
        temp = data
    }

    render(){

        const { Header, Row, HeaderCell, Body} = Table;

        return(
            <div style={{ backgroundImage:"url('https://images.pexels.com/photos/1095814/pexels-photo-1095814.jpeg')", 
            height:'100vh',
            backgroundSize: 'cover',
            backgroundRepeat: 'no-repeat'}}>
        <Layout>

            <link rel="stylesheet" href="//cdnjs.cloudflare.com/ajax/libs/semantic-ui/2.2.12/semantic.min.css"/>
            <span> </span>
            <h3 align="center" style={{color:'#ffffff', fontWeight:'bold'}} >Container Details</h3>
            <div style={{marginLeft:'80px', marginRight:'80px'}}>
            <div style={{color:'#ffffff', fontWeight:'bold', width:'200px'}}>
                <Form>
                        <label style={{color:'#ffffff', fontWeight:'bold'}}> Status </label>
                        <Form.Field
                        control={Select}
                        options={option2}
                        onChange={this.onClickedStatus}/>
                        </Form>
             </div>
                    
            <Table>
                <Header>
                    <Row>
                        <HeaderCell>ID</HeaderCell>
                        <HeaderCell>Container ID</HeaderCell>
                        <HeaderCell>Halal status</HeaderCell>
                        <HeaderCell>Status</HeaderCell>
                        <HeaderCell>Start Date</HeaderCell>
                        <HeaderCell>End Date</HeaderCell>
                        <HeaderCell>Product Name</HeaderCell>
                        <HeaderCell>Quantity</HeaderCell>
                    </Row>
                </Header>
                <Body>{this.renderRowsCont()}</Body>
            </Table>
            </div>
            <br></br>
            <div>
            {this.getContainerInfo()}
            <DataTable columns={columns} data={data} />
            </div>
            </Layout>
            </div>
            
        );
    }
}

export default showCont;
