import React, { Component } from 'react';
import { Form, Button, Message } from 'semantic-ui-react';
import Layout from '../../components/Layout';
import container from '../../ethereum/container';
import web3 from '../../ethereum/web3';

class ContainerForm extends Component {

    state = {
        ContainerID:'',
        HalalStatus:'',
        WarehouseNo:'',
        ProductName:'',
        StartDate:'',
        EndDate:'',
        Quantity:'',
        errorMessage:'',
        loading:false
    }

//0xda4Eb9484E64AD54FBd3566dd8F3035e8B5219BB


    onSubmit = async (event) => {
        event.preventDefault();

        this.setState({ loading: true, errorMessage: '' });

    try{
        const accounts = await web3.eth.getAccounts();

        await container.methods.registerContainer(this.state.ContainerID, this.state.HalalStatus, this.state.WarehouseNo,this.state.ProductName,this.state.StartDate, this.state.EndDate, this.state.Quantity).send({from: accounts [0]});

    } catch (err) {
        this.setState({ errorMessage: err.message });
     }

     this.setState({ loading: false });
    };

    render() {
        return (
        <html>        
        <Layout>
            <link rel="stylesheet" href="//cdnjs.cloudflare.com/ajax/libs/semantic-ui/2.2.12/semantic.min.css"/>
            
            <body>
            <div class="ui container">
                <div class="equal width fields">
                <h2 align="center">Container Form</h2>
                <Form onSubmit={this.onSubmit} error={!!this.state.errorMessage}>
                    <Form.Field>
                        <label>Container ID</label>
                        <input 
                            value={this.state.ContainerID}
                            onChange={event =>
                                this.setState({ContainerID: event.target.value})}
                        />
                        <label>Warehouse ID</label>
                        <input 
                            label="In square fit"
                            labelPosition="right"
                            value={this.state.WarehouseNo}
                            onChange={event =>
                                this.setState({WarehouseNo: event.target.value})}
                        />
                        <label>Product Name</label>
                        <input
                            value={this.state.ProductName}
                            onChange={event =>
                                this.setState({ProductName: event.target.value})}
                        />
                        <label>Halal Status</label>
                        <input 
                            value={this.state.HalalStatus}
                            onChange={event =>
                                this.setState({HalalStatus: event.target.value})}
                        />
                        <label>Start Date</label>
                        <input 
                            value={this.state.StartDate}
                            onChange={event =>
                                this.setState({StartDate: event.target.value})}
                        />
                        <label>End Date</label>
                        <input 
                            value={this.state.EndDate}
                            onChange={event =>
                                this.setState({EndDate: event.target.value})}
                        />
                        <label>Quantity</label>
                        <input 
                            value={this.state.Quantity}
                            onChange={event =>
                                this.setState({Quantity: event.target.value})}
                        />
                    </Form.Field>

                    <Message error header="Oops!" content={this.state.errorMessage}/>
                    <Button loading= {this.state.loading} primary>Submit</Button>
                </Form>
                </div>
                </div>
            </body>
        </Layout>
        </html>
        );
    }
}

export default ContainerForm;
