import React, { Component } from 'react';
import { Form, Button } from 'semantic-ui-react';
import warehouse from '../../ethereum/warehouse';
import web3 from '../../ethereum/web3';
import { Link, Router } from '../../routes';

class WarehouseForm extends Component {

    state = {
        WarehouseID:'',
        WarehouseSize:'',
        WarehouseAddress:'',
        AreaAmount:''

    }


    onSubmit = async (event) => {
        event.preventDefault();

        const accounts = await web3.eth.getAccounts();

        await warehouse.methods.setWarehouseInfo(this.state.WarehouseID, this.state.WarehouseSize, this.state.WarehouseAddress,this.state.AreaAmount).send({from: accounts [0]});

    };

    render() {
        return (
        <html>
            <head>
                <meta charset="utf-8"/>
                
                <link rel="stylesheet" href="//cdnjs.cloudflare.com/ajax/libs/semantic-ui/2.2.12/semantic.min.css"/>
            </head>

            <body>
            <div class="ui container">
                <div class="equal width fields">
                <h2 align="center">Warehouse Form</h2>
                <Form onSubmit={this.onSubmit}>
                    <Form.Field>
                        <label>Warehouse ID</label>
                        <input 
                            value={this.state.WarehouseID}
                            onChange={event =>
                                this.setState({WarehouseID: event.target.value})}
                        />
                        <label>Warehouse Size</label>
                        <input 
                            label="In square fit"
                            labelPosition="right"
                            value={this.state.WarehouseSize}
                            onChange={event =>
                                this.setState({WarehouseSize: event.target.value})}
                        />
                        <label>Address</label>
                        <input
                            value={this.state.WarehouseAddress}
                            onChange={event =>
                                this.setState({WarehouseAddress: event.target.value})}
                        />
                        <label>Amount of Area</label>
                        <input 
                            value={this.state.AreaAmount}
                            onChange={event =>
                                this.setState({AreaAmount: event.target.value})}
                        />
                    </Form.Field>

                    <Button primary>Submit</Button>
                </Form>
                </div>
                </div>
            </body>
        </html>
        );
    }
}

export default WarehouseForm;
