import React, { Component } from 'react';
import { Form, Button } from 'semantic-ui-react';
import container from '../../ethereum/container';

class FormCreate extends Component {

    static async getInitialProps(){
        const info = await container.methods.GetUserInfo().call();

        return {
            ContainerNo: info[0],
            WarehouseNo: info[1],
            ProductName: info[2],
            HalalStatus: info[3],
            Quantity: info[4]
        };
    }

    renderInfo() {
        const{
            ContainerNo,
            WarehouseNo,
            ProductName,
            HalalStatus,
            Quantity
        } = this.props;

        const items = [
            {
                header: ContainerNo,
                description:'View details',
                fluid: true
            }

        ];
        return<Card.Group items={items}/>
    }
 
renderInfo() {
        const{
            ContainerNo,
            WarehouseNo,
            ProductName,
            HalalStatus,
            Quantity
        } = this.props;

        const items = [
            {
                header: ContainerNo,
                meta:'Container ID',
                description:'Each of the container having own unique ID',
                style:{ overflowWrap:'break-word'}
            }

            /*{
                header: WarehouseNo,
                meta:'Warehouse ID',
                description:'Each of the warehouse having own unique ID'
            },

            {
                header: ProductName,
                meta:'The name of product',
                description:'The product name'
            },

            {
                header: HalalStatus,
                meta:'Halal status',
                description:'Halal status of the product'
            },

            {
                header: Quantity,
                meta:'Quantity of stock',
                description:'The quantity of stock'
            }*/

        ];
        return<Card.Group items={items}/>
    }

    render(){
        return (
        <div>
            <link rel="stylesheet" href="//cdnjs.cloudflare.com/ajax/libs/semantic-ui/2.2.12/semantic.min.css"/>
            {this.renderInfo()}




        </div>
            );
    }
}

export default FormCreate;
