import React, { Component } from 'react';
import { Form, Button, Message } from 'semantic-ui-react';
import Layout from '../../components/Layout';
import container from '../../ethereum/container';
import web3 from '../../ethereum/web3';

class ContainerForm extends Component {

    state = {
        ContainerNo:'',
        HalalStatus:'',
        FinalDes:'',
        ProductName:'',
        Quantity:'',
        errorMessage:'',
        loading:false
    }

   /* static async getInitialProps(){

        const defCount = await Input.methods.getdefContainerCount().call();
       

        const defcontainer = await Promise.all(
            Array(parseInt(defCount)).fill().map((element, index) => {
                return Input.methods.defcontainer(index).call()
            })
        );

        {defcontainer.complete ? defcontainer[0] : (
            <input></input>
        )}

    }*/

    onSubmit = async (event) => {
        event.preventDefault();

        this.setState({ loading: true, errorMessage: '' });

    try{
        const accounts = await web3.eth.getAccounts();

        await container.methods.registerContainer(this.state.ContainerNo, this.state.HalalStatus, this.state.FinalDes,this.state.ProductName,this.state.Quantity).send({from: accounts [0]});

    } catch (err) {
        this.setState({ errorMessage: err.message });
     }

     this.setState({ loading: false });
    };

    render() {

        return (
        <html>        
        <Layout>
            <link rel="stylesheet" href="//cdnjs.cloudflare.com/ajax/libs/semantic-ui/2.2.12/semantic.min.css"/>
            
            <body style={{backgroundColor:'#91d0fa'}}>
            <div class="ui container">
                <div class="equal width fields">
                <h2 align="center">Container Form</h2>
                <Form onSubmit={this.onSubmit} error={!!this.state.errorMessage}>
                    <Form.Field>
                        <label>Container ID</label>
                        <input 
                            value={this.state.ContainerNo}
                            onChange={event =>
                                this.setState({ContainerNo: event.target.value})}
                        />
                        <label>Final Destination</label>
                        <input 
                            label="In square fit"
                            labelPosition="right"
                            value={this.state.FinalDes}
                            onChange={event =>
                                this.setState({FinalDes: event.target.value})}
                        />
                        <label>Product Name</label>
                        <input
                            value={this.state.ProductName}
                            onChange={event =>
                                this.setState({ProductName: event.target.value})}
                        />
                        <label>Halal Status</label>
                        <input 
                            value={this.state.HalalStatus}
                            onChange={event =>
                                this.setState({HalalStatus: event.target.value})}
                        />
                        <label>Quantity</label>
                        <input 
                            value={this.state.Quantity}
                            onChange={event =>
                                this.setState({Quantity: event.target.value})}
                        />
                    </Form.Field>

                    <Message error header="Oops!" content={this.state.errorMessage}/>
                    <Button loading= {this.state.loading} primary>Submit</Button>
                </Form>
                </div>
                </div>
            </body>
        </Layout>
        </html>
        );
    }
}

export default ContainerForm;
