import React, { Component } from 'react';
import { Form, Button, Message, FormField, Select, Input } from 'semantic-ui-react';
import Layout from '../../components/Layout';
import container from '../../ethereum/container';
import web3 from '../../ethereum/web3';

const options = [
    { key: 'H', text: 'Halal', value: 'Halal' },
    { key: 'NH', text: 'Non-Halal', value: 'Non-Halal' },
]

class ContainerForm extends Component {

    state = {
        ContainerNo:'',
        HalalStatus:'',
        Description:'',
        Supplier:'',
        Booked:'',
        Size:'',
        FinalDes:'',
        ProductName:'',
        Quantity:'',
        errorMessage:'',
        loading:false
    };

    handleHalalStatus = (e, { value }) => this.setState({ HalalStatus: value })


    onSubmit = async event => {
        event.preventDefault();

        const {ContainerNo, HalalStatus, FinalDes, ProductName, Quantity, Description, Supplier, Booked, Size} = this.state;

        this.setState({ loading: true, errorMessage: '' });

    try{
        const accounts = await web3.eth.getAccounts();

        await container.methods.registerContainer(ContainerNo, HalalStatus, FinalDes,ProductName,Quantity, Description, Supplier, Booked, Size).send({from: accounts [0]});

    } catch (err) {
        this.setState({ errorMessage: err.message });
     }

     this.setState({ ContainerNo:'',
     HalalStatus:'', FinalDes:'', ProductName:'',Quantity:'', errorMessage:'', Description:'',Supplier:'',Booked:'', Size:'',loading:false });
};

    render() {
        return (
            
        <Layout style={{marginLeft:''}}>
            <link rel="stylesheet" href="//cdnjs.cloudflare.com/ajax/libs/semantic-ui/2.2.12/semantic.min.css"/>
            
            <body style={{backgroundColor:'#91d0fa'}}>
            <div class="ui container">
                <div class="equal width fields">
                <h2 align="center">Container Form</h2>
                <Form onSubmit={this.onSubmit} error={!!this.state.errorMessage}>
                    
                        <Form.Field>
                        <label>Container ID</label>
                        <Input 
                            value={this.state.ContainerNo}
                            onChange={event =>
                                this.setState({ContainerNo: event.target.value})}
                        />
                        </Form.Field>
                        <br/>
                        <Form.Field>
                        <label>Description</label>
                        <Input 
                            value={this.state.Description}
                            onChange={event =>
                                this.setState({Description: event.target.value})}
                        />
                        </Form.Field>
                        <br/>
                        <Form.Field>
                        <label>Size</label>
                        <Input 
                            value={this.state.Size}
                            onChange={event =>
                                this.setState({Size: event.target.value})}
                        />
                        </Form.Field>
                        <br/>
                        <Form.Field>
                        <label>Supplier</label>
                        <Input 
                            value={this.state.Supplier}
                            onChange={event =>
                                this.setState({Supplier: event.target.value})}
                        />
                        </Form.Field>
                        <br/>
                        <FormField>
                        <label>Final Destination</label>
                        <Input 
                            label="In square fit"
                            labelPosition="right"
                            value={this.state.FinalDes}
                            onChange={event =>
                                this.setState({FinalDes: event.target.value})}
                        />
                        </FormField>
                        <br/>
                        <Form.Field>
                        <label>Product Name</label>
                        <Input
                            value={this.state.ProductName}
                            onChange={event =>
                                this.setState({ProductName: event.target.value})}
                        />
                        </Form.Field>
                        <br/>
                        <Form.Field>
                        <label>Container Status</label>
                        <Input 
                            value={this.state.Booked}
                            onChange={event =>
                                this.setState({Booked: event.target.value})}
                        />
                        </Form.Field>
                        <br/>
                        <Form.Field
                        label='Halal Status'
                        control={Select}
                        options={options}
                        onChange={this.handleHalalStatus}/>
                        <br/>
                        <Form.Field>
                        <label>Quantity</label>
                        <Input 
                            value={this.state.Quantity}
                            onChange={event =>
                                this.setState({Quantity: event.target.value})}
                        />
                        </Form.Field>
                        <br/>
                    
                    <Message error header="Oops!" content={this.state.errorMessage}/>
                    <Button loading= {this.state.loading} primary>Submit</Button>
                </Form>
                </div>
                </div>
            </body>
        </Layout>
        
        );
    }
}

export default ContainerForm;
