import React, { Component } from 'react';
import { Card, Form, Button, Table } from 'semantic-ui-react';
import container from '../../ethereum/container';
import { Container } from 'semantic-ui-react';
import Layout from '../../components/Layout';


class ReviewInfo extends Component {

    /*
    static async getInitialProps(){
        const requestCount = await container.methods.GetUserInfo().call();

        const requests = await Promise.all(
            Array(requestCount)
            .fill()
            .map((element, index) => {
                return container.methods.requests(index).call();
            })
        );

        return {
        requests, requestCount, ContainerNo
        };
    }

    renderRow(){
        return this.props.requests.map((request, index) => {
            return (
                <RequestRow
                    ContainerNo={index}
                    WarehouseNo={index}
                    request={request}
                    address={this.props.address}
                />
            );
        });
    }*/

    render(){
/*
        const { Header, Row, HeaderCell, Body} = Table;

        return(
            <Layout>
            <Container>
            <link rel="stylesheet" href="//cdnjs.cloudflare.com/ajax/libs/semantic-ui/2.2.12/semantic.min.css"/>
            <span> </span>
            <h3 align="center">Container Details</h3>
            <Table>
                <Header>
                    <Row>
                        <HeaderCell>Container ID</HeaderCell>
                        <HeaderCell>Warehouse ID</HeaderCell>
                        <HeaderCell>Product Name</HeaderCell>
                        <HeaderCell>Halal Status</HeaderCell>
                        <HeaderCell>Quantity</HeaderCell>
                    </Row>
                </Header>
                <Body></Body>
            </Table>
            </Container>
            </Layout>
        );*/


        return(
        <Layout>
            
                <link rel="stylesheet" href="//cdnjs.cloudflare.com/ajax/libs/semantic-ui/2.2.12/semantic.min.css"/>

        <span> </span>
        <h3 align="center">Container Details</h3>
        <table class="container table">
            <thead>
                <tr><th>Container ID</th>
                    <th>Container Size (ft)</th>
                    <th>Current Halal Status</th>
                    <th>Supplier of Container</th>
                </tr></thead>
            <tbody>
                <tr>
                <td data-label="Container ID">A008</td>
                <td data-label="Container Size (ft)">2000</td>
                <td data-label="Current Halal Status">Halal</td>
                <td data-label="Supplier of Container">Tiong Nam</td>
                </tr>
            </tbody>
        </table>


        <span> </span>
        <h3 align="center">Warehouse Details</h3>
        <table class="container table">
            <thead>
                <tr><th>Warehouse ID</th>
                    <th>Warehouse Size (square meter)</th>
                    <th>Address</th>
                    <th>Amount of Area</th>
                </tr></thead>
            <tbody>
                <tr>
                <td data-label="Warehouse ID">A008</td>
                <td data-label="Warehouse Size (square meter)">2000</td>
                <td data-label="Address">KL</td>
                <td data-label="Amount of Area">200</td>
                </tr>
            </tbody>
        </table>
        </Layout>
        );
    }
      
}


export default ReviewInfo;
