import React, { Component } from 'react';
import { Form, Button, Message, FormField, Select, Input, Table, Grid, GridColumn } from 'semantic-ui-react';
import Layout from '../../components/Layout';
import inputdata from '../../ethereum/container';
import web3 from '../../ethereum/web3';

const options = [
    { key: 'H', text: 'Halal', value: 'Halal' },
    { key: 'NH', text: 'Non-Halal', value: 'Non-Halal' },
]


class HalalStatusForm extends Component {

   
    state = {
        ContainerID:'',
        HalalStatus:'',
        booked:'',
        ProductName:'',
        Quantity:'',
        Remark:'',
        errorMessage:'',
        value:'',
        loading:false
    };

    handleHalalStatus = (e, { value }) => this.setState({ HalalStatus: value })

    static async getInitialProps(){
     
        
        const reCount = await inputdata.methods.getRemarkCount().call();

        const contCount = await inputdata.methods.getContainerCount().call();

        const remarks = await Promise.all(
            Array(parseInt(reCount)).fill().map((element, index) => {
                return inputdata.methods.remarks(index).call()
            })
        );

        const container = await Promise.all(
          Array(parseInt(contCount)).fill().map((element, index) => {
              return inputdata.methods.container(index).call()
          })
      );

        return {remarks, reCount, container, contCount};

    }


  //   getContainerStat(event){
       
  //     return this.props.container.find((request, index) => {
  //     if(this.state.value == null || this.state.value == ''){
  //       for (var i = 0; i < this.props.contCount; i++){
          
  //         if(request._booked === '' ){
            
  //             this.setState({_StartDate:request._StartDate, _EndDate:request._EndDate, _ProductName:request._ProductName, _HalalStatus:request._HalalStatus,  _Quantity:request._Quantity})
              
  //            this.state.value ='';
  //            return true;
  //          } else {
            
  //            this.state.value = 'Please enter a valid and available Container ID';
  //            return false;
  //         }
  //        }
  //     }
  //     });
  // }

    getContainerInfo(event){
       
        return this.props.container.find((request, index) => {
        for (var i = 0; i < this.props.contCount; i++){
             if(event.target.value ===  request._ContainerNo){

                this.setState({ContainerID: request._ContainerNo,HalalStatus:request._HalalStatus, booked: request._booked, ProductName:request._ProductName, Quantity: request._Quantity })
                
                this.state.value ='';
                
                return true;
              } else {
                
                this.state.value = 'Please enter a valid Container ID';
                return false;
             }
            }
        });
    }
    

    onSubmit = async event => {
        event.preventDefault();

        const {ContainerID, HalalStatus, booked, ProductName, Quantity, Remark } = this.state;

        this.setState({ loading: true, errorMessage: '' });

    try{
        const accounts = await web3.eth.getAccounts();

        await inputdata.methods.registerContainer(ContainerID, HalalStatus, booked, ProductName, Quantity, Remark).send({from: accounts [0]});

    } catch (err) {
        this.setState({ errorMessage: err.message });
     }

     this.setState({ ContainerID:'', HalalStatus:'', booked:'', ProductName:'', Quantity:'', Remark:'', errorMessage:'', loading:false });
};

    render() {
        return (
            
        <Layout style={{marginLeft:'50px', marginRight:'50px', marginTop:'50px'}}>
            <link rel="stylesheet" href="//cdnjs.cloudflare.com/ajax/libs/semantic-ui/2.2.12/semantic.min.css"/>
            
            <body style={{ backgroundImage:"url('https://images.pexels.com/photos/1095814/pexels-photo-1095814.jpeg')", 
            height:'100vh',
            backgroundSize: 'cover',
            backgroundRepeat: 'no-repeat'}}>
            <div style={{display:'flex', flexDirection: 'row'}}>
            <div style={{width:'100%'}}>
            <div class="ui container">
                <div class="equal width fields">
                <h2 align="center" style={{color:'#ffffff', fontWeight:'bold'}}>Halal Status Update Form</h2>
                <Form onSubmit={this.onSubmit} error={!!this.state.errorMessage}>
                    
                        <Form.Field>
                        <label style={{color:'#ffffff', fontWeight:'bold'}}>Container ID</label>
                        <Input type="text" id ='Cid' autoComplete="off"
                            value={this.state.ContainerID}
                            onChange={event =>{
                                this.setState({ContainerID: event.target.value});this.getContainerInfo(event)
                                //this.getContainerStat(event)
                                }}placeholder="C001"/>
                        
                        </Form.Field>
                        
                        <p id ='error' style={{color:'red', fontWeight:'bold'}}>{this.state.value}</p>
                        <br/>
                        <Form.Field>
                        <label style={{color:'#ffffff', fontWeight:'bold'}}>Halal Status</label>
                        <Input 
                            value={this.state.HalalStatus}
                            onChange={event =>
                                this.setState({HalalStatus: event.target.value})}placeholder="Halal/Non-Halal"
                        />
                        </Form.Field>
                        <Form.Field>
                        <label style={{color:'#ffffff', fontWeight:'bold'}}>Status</label>
                        <Input 
                            value={this.state.booked}
                            onChange={event =>
                                this.setState({booked: event.target.value})}
                        />
                        </Form.Field>
                        <br/>
                        <Form.Field>
                        <label style={{color:'#ffffff', fontWeight:'bold'}}>Product Name</label>
                        <Input
                            value={this.state.ProductName}
                            onChange={event =>
                                this.setState({ProductName: event.target.value})}placeholder="Fish"
                        />
                        </Form.Field>
                        <br/>
                        <Form.Field>
                        <label style={{color:'#ffffff', fontWeight:'bold'}}>Quantity</label>
                        <Input 
                            value={this.state.Quantity}
                            onChange={event =>
                                this.setState({Quantity: event.target.value})}placeholder="in Kg"
                        />
                        </Form.Field>
                        <br/>
                        <Form.Field>
                        <label style={{color:'#ffffff', fontWeight:'bold'}}>Remarks</label>
                        <Input 
                            value={this.state.Remark}
                            onChange={event =>
                                this.setState({Remark: event.target.value})}
                        />
                        </Form.Field>
                        <br/>
                    
                    <Message error header="Oops!" content={this.state.errorMessage}/>
                    <Button style={{marginBottom:'20px'}} loading= {this.state.loading} primary>Submit</Button>
                </Form>
                </div>
                </div>
                </div>
        </div>
        </body>
        </Layout>
        
        );
    }
}

export default HalalStatusForm;
