import React, { Component } from 'react';
import container from '../ethereum/container';

class ReviewInfo extends Component {
    static async gtetInitialProps(){
        const info = await container.methods.GetUserInfo().call();

        return { info };
    }

    render(){
        return (<div>{this.props.info[0]}</div>);
        
    }
}

export default ReviewInfo;
