import React, { Component } from 'react';
import { Button, Card } from 'semantic-ui-react';
import container from '../ethereum/container';
import { Container } from 'semantic-ui-react';
import Layout from '../components/Layout';


class ReviewIndex extends Component {

    static async getInitialProps(){
        const info = await container.methods.getContainerCount().call();

        return {
            ContainerNo: info[0],
            WarehouseNo: info[1],
            ProductName: info[2],
            HalalStatus: info[3],
            Quantity: info[4]
        };

        
    }

    static async getInitialProps(){
        const record = await container.methods.getWarehouseCount().call();

        return {
            
            WarehouseID: record[0]
            
        };
        
    }

    renderContainer() {
        const{
            ContainerNo,
            WarehouseNo,
            ProductName,
            HalalStatus,
            Quantity
        } = this.props;

        const items = [
            {
                header: ContainerNo,
                description:<a>View details</a>,
                fluid: true
            }

        ];
        return<Card.Group item1={item1}/>
    }

    renderWarehouse() {
        const{
            WarehouseID
        } = this.props;

        const items = [
            {
                header: WarehouseID,
                description:<a>View details</a>,
                fluid: true
            }

        ];
        return<Card.Group items={items}/>
    }

    render(){
        return (
        <html>
        <Layout>
         <link rel="stylesheet" href="//cdnjs.cloudflare.com/ajax/libs/semantic-ui/2.2.12/semantic.min.css"/>

        <body>
        <div style={{marginLeft:'80px', marginRight:'80px'}}>
            <h2>Transaction Record for Container</h2>
            {this.renderContainer()}
            <Button content="Create More Form" icon="add circle" primary floated="left"></Button>
            <br></br>
            <h2>Transaction Record for Warehouse</h2>
            {this.renderWarehouse()}
            <Button content="Create More Form" icon="add circle" primary floated="left"></Button>
        </div>
        
        </body>
        </Layout>
        </html>
        );
    }

}
export default ReviewIndex;
