import React, { Component } from 'react';
import { Form, Button, Message } from 'semantic-ui-react';
import Layout from '../../components/Layout';
import container from '../../ethereum/container';
import web3 from '../../ethereum/web3';

class defContainerForm extends Component {

    state = {
        WarehouseID:'',
        WarehouseSize:'',
        AreaCode:'',
        WarehouseAddress:'',
        errorMessage:'',
        loading:false
    }


    onSubmit = async (event) => {
        event.preventDefault();

        this.setState({ loading: true, errorMessage: '' });

    try{
        const accounts = await web3.eth.getAccounts();

        await container.methods.defineWarehouse(this.state.WarehouseID, this.state.WarehouseSize, this.state.AreaCode, this.state.WarehouseAddress).send({from: accounts [0]});

    } catch (err) {
        this.setState({ errorMessage: err.message });
     }

     this.setState({ loading: false });
    };

    render() {
        return (
        <html>        
        <Layout>
            <link rel="stylesheet" href="//cdnjs.cloudflare.com/ajax/libs/semantic-ui/2.2.12/semantic.min.css"/>
            
            <body style={{ backgroundImage:"url('https://images.pexels.com/photos/1095814/pexels-photo-1095814.jpeg')", 
            height:'100vh',
            backgroundSize: 'cover',
            backgroundRepeat: 'no-repeat'}}>
            <div class="ui container">
                <div class="equal width fields">
                <h2 align="center">New Warehouse Form</h2>
                <Form onSubmit={this.onSubmit} error={!!this.state.errorMessage}>
                    <Form.Field>
                        <label>Warehouse ID</label>
                        <input 
                            value={this.state.WarehouseID}
                            onChange={event =>
                                this.setState({WarehouseID: event.target.value})}
                        />
                        <label>Size</label>
                        <input
                            value={this.state.WarehouseSize}
                            onChange={event =>
                                this.setState({WarehouseSize: event.target.value})}
                        />
                        <label>Area Code</label>
                        <input 
                            value={this.state.AreaCode}
                            onChange={event =>
                                this.setState({AreaCode: event.target.value})}
                        />
                        <label>Warehouse Address</label>
                        <input 
                            label="In square fit"
                            labelPosition="right"
                            value={this.state.WarehouseAddress}
                            onChange={event =>
                                this.setState({WarehouseAddress: event.target.value})}
                        />
                    </Form.Field>

                    <Message error header="Oops!" content={this.state.errorMessage}/>
                    <Button loading= {this.state.loading} primary>Submit</Button>
                </Form>
                </div>
                </div>
            </body>
        </Layout>
        </html>
        );
    }
}

export default defContainerForm;
