import React, { Component } from 'react';
import { Form, Button, Message } from 'semantic-ui-react';
import Layout from '../../components/Layout';
import container from '../../ethereum/container';
import web3 from '../../ethereum/web3';

class defContainerForm extends Component {

    state = {
        ContainerNo:'',
        HalalStatus:'',
        Description:'',
        Supplier:'',
        Size:'',
        errorMessage:'',
        loading:false
    }


    onSubmit = async (event) => {
        event.preventDefault();

        this.setState({ loading: true, errorMessage: '' });

    try{
        const accounts = await web3.eth.getAccounts();

        await container.methods.registerContainer(this.state.ContainerNo, this.state.HalalStatus, this.state.Description,this.state.Supplier,this.state.Size).send({from: accounts [0]});

    } catch (err) {
        this.setState({ errorMessage: err.message });
     }

     this.setState({ loading: false });
    };

    render() {
        return (
        <html>        
        <Layout>
            <link rel="stylesheet" href="//cdnjs.cloudflare.com/ajax/libs/semantic-ui/2.2.12/semantic.min.css"/>
            
            <body>
            <div class="ui container">
                <div class="equal width fields">
                <h2 align="center">New Container Form</h2>
                <Form onSubmit={this.onSubmit} error={!!this.state.errorMessage}>
                    <Form.Field>
                        <label>Container ID</label>
                        <input 
                            value={this.state.ContainerNo}
                            onChange={event =>
                                this.setState({ContainerNo: event.target.value})}
                        />
                        <label>Halal Status</label>
                        <input 
                            label="In square fit"
                            labelPosition="right"
                            value={this.state.HalalStatus}
                            onChange={event =>
                                this.setState({HalalStatus: event.target.value})}
                        />
                        <label>Description</label>
                        <input
                            value={this.state.Description}
                            onChange={event =>
                                this.setState({ContainerAddr: event.target.value})}
                        />
                        <label>Container's Supplier</label>
                        <input 
                            value={this.state.Supplier}
                            onChange={event =>
                                this.setState({Supplier: event.target.value})}
                        />
                        <label>Size</label>
                        <input 
                            value={this.state.Size}
                            onChange={event =>
                                this.setState({Size: event.target.value})}
                        />
                    </Form.Field>

                    <Message error header="Oops!" content={this.state.errorMessage}/>
                    <Button loading= {this.state.loading} primary>Submit</Button>
                </Form>
                </div>
                </div>
            </body>
        </Layout>
        </html>
        );
    }
}

export default defContainerForm;
