import React, { Component } from 'react';
import { Form, Button, Message, FormField, Select, Input, Table } from 'semantic-ui-react';
import Layout from '../../components/Layout';
import container from '../../ethereum/container';
import web3 from '../../ethereum/web3';

const options = [
    { key: 'H', text: 'Halal', value: 'Halal' },
    { key: 'NH', text: 'Non-Halal', value: 'Non-Halal' },
]

class ContainerForm extends Component {

    state = {
        _ContainerNo:'',
        _HalalStatus:'',
        _booked:'',
        _StartDate:'',
        _EndDate:'',
        _ProductName:'',
        _Quantity:'',
        errorMessage:'',
        loading:false
    };

    handleHalalStatus = (e, { value }) => this.setState({ HalalStatus: value })

    static async getInitialProps(){
     
        
        const defCount = await container.methods.getdefContainerCount().call();

        const defcontainer = await Promise.all(
            Array(parseInt(defCount)).fill().map((element, index) => {
                return container.methods.defcontainer(index).call()
            })
        );

        return {defcontainer, defCount};

    }

    getContainerInfo(event){
       const {request} = this.props;
        return this.props.defcontainer.map((request, index) => {
         for (var i = 0; i < this.props.defCount; i++){
             if(event.target.value ==  request.ContainerNo){
                 this.setState({_HalalStatus:request.HalalStatus})
                
             }
         }
        });
    }

    renderTable(event){
        console.log('Hi')
        return this.props.defcontainer.map((request, index) => {
            for (var i = 0; i < this.props.defCount; i++){
                if(event.target.value ==  request.ContainerNo){
            return(
                
                /*{ <Table>
                <h1>Hello</h1>
                <tr>
                    <th>Container ID</th>
                    <th>Description</th>
                    <th>Halal Status</th>
                    <th>Supplier</th>
                    <th>Size</th>
                </tr>
                <tr>
                    <td>{request.ContainerNo}</td>
                    <td>{request.Description}</td>
                    <td>{request.HalalStatus}</td>
                    <td>{request.Supplier}</td>
                    <td>{request.Size}</td>
                </tr>
            </Table> }*/
            <h1>Hello</h1>
                
        )
        }
        
        }
        });
        
    }

    onSubmit = async event => {
        event.preventDefault();

        const {_ContainerNo, _HalalStatus, _ProductName, _Quantity, _booked, _StartDate, _EndDate} = this.state;

        this.setState({ loading: true, errorMessage: '' });

    try{
        const accounts = await web3.eth.getAccounts();

        await container.methods.registerContainer(_ContainerNo, _HalalStatus,_ProductName,_Quantity, _booked, _StartDate, _EndDate).send({from: accounts [0]});

    } catch (err) {
        this.setState({ errorMessage: err.message });
     }

     this.setState({ _ContainerNo:'',
     _HalalStatus:'', _ProductName:'',_Quantity:'', errorMessage:'',_booked:'', _StartDate:'', _EndDate:'', loading:false });
};

    render() {
        return (
            
        <Layout style={{marginLeft:'50px', marginRight:'50px', marginTop:'50px'}}>
            <link rel="stylesheet" href="//cdnjs.cloudflare.com/ajax/libs/semantic-ui/2.2.12/semantic.min.css"/>
            
            <body style={{backgroundColor:'#91d0fa'}}>
            <div style={{display:'flex', flexDirection: 'row'}}>
            <div style={{width:'50%', paddingLeft:'50px'}}>
            <div class="ui container">
                <div class="equal width fields">
                <h2 align="center">Delivery Form</h2>
                <Form onSubmit={this.onSubmit} error={!!this.state.errorMessage}>
                    
                        <Form.Field>
                        <label>Container ID</label>
                        <Input type="text" id ='Cid' autoComplete="off"
                            value={this.state._ContainerNo}
                            onChange={event =>{
                                this.setState({_ContainerNo: event.target.value});this.getContainerInfo(event);this.renderTable(event)}}placeholder="C001"/>
                        
                        </Form.Field>
                        <br/>
                        <Form.Field>
                        <label>Start Date</label>
                        <Input type='date'
                        value={this.state._StartDate}
                        onChange={event =>
                            this.setState({_StartDate: event.target.value})}placeholder="DD/MM/YYYY"/>
                        
                        </Form.Field>
                        <br/>
                        <Form.Field>
                        <label>End Date</label>
                        <Input type = 'date'
                            value={this.state._EndDate}
                            onChange={event =>
                                this.setState({_EndDate: event.target.value})}
                        />
                        </Form.Field>
                        <br/>
                        <Form.Field>
                        <label>Product Name</label>
                        <Input
                            value={this.state._ProductName}
                            onChange={event =>
                                this.setState({_ProductName: event.target.value})}placeholder="Fish"
                        />
                        </Form.Field>
                        <Form.Field>
                        <div 
                            value={this.state._booked}
                            onChange={event =>
                                this.setState({_booked: event.target.value})}
                        />
                        </Form.Field>
                        
                        <Form.Field>
                        <label>Halal Status</label>
                        <Input 
                            value={this.state._HalalStatus}
                            onChange={event =>
                                this.setState({HalalStatus: event.target.value})}placeholder="Halal/Non-Halal"
                        />
                        </Form.Field>
                        <br/>
                        <Form.Field>
                        <label>Quantity</label>
                        <Input 
                            value={this.state._Quantity}
                            onChange={event =>
                                this.setState({_Quantity: event.target.value})}placeholder="in Kg"
                        />
                        </Form.Field>
                        <br/>
                    
                    <Message error header="Oops!" content={this.state.errorMessage}/>
                    <Button style={{marginBottom:'20px'}} loading= {this.state.loading} primary>Submit</Button>
                </Form>
                </div>
                </div>
                </div>
                 
        
                
        </div>
        </body>
        </Layout>
        
        );
    }
}

export default ContainerForm;
