import React, { Component } from 'react';
import { Button, Card } from 'semantic-ui-react';
import Input from '../ethereum/container';
import { Link } from '../routes';
import Layout from '../components/Layout';

class ReviewIndex extends Component {

    static async getInitialProps(){
        const info = await Input.methods.getContainerCount().call();
        const info2 = await Input.methods.getWarehouseCount().call();
        const info3 = await Input.methods.getdefContainerCount().call();

        const defcontainer = await Promise.all(
            Array(parseInt(info3)).fill().map((element, index) => {
                return Input.methods.defcontainer(index).call()
            })
        );

        return { Count: info[0],
                Count2: info2[0],
                Count3: info3[0], 
                Regcount: defcontainer[8]
        };
    }

    renderRegContainer(){

        const {
            Regcount
        }=this.props;

        const items = [
            {
                header: 'Registered Containers',
                meta: Regcount,
                description: 'The quantity of registered containers will be display here'
            }
        ]
    }

    renderContainer() {
        const{
            Count
        } = this.props;

        const items = [
            {
                header: Count,
                description:(
                <Link route= "form/">
                <a>View details</a> 
                </Link>),
                fluid: true
            }
        ];
        return<Card.Group items={items}/>
    }
    

    renderWarehouse() {
        const{
            Count2
        } = this.props;

        const items = [
            {
                header: Count2,
                description:<a>View details</a>,
                fluid: true
            }

        ];
        return<Card.Group items={items}/>
    }

    renderdefContainer() {
        const{
            Count3
        } = this.props;

        const items = [
            {
                header: Count3,
                description:<a>View details</a>,
                fluid: true
            }
        ];
        return<Card.Group items={items}/>
    }

    render(){
        return (
        <html>
        <Layout>
         <link rel="stylesheet" href="//cdnjs.cloudflare.com/ajax/libs/semantic-ui/2.2.12/semantic.min.css"/>

        <body>
        <div style={{marginLeft:'80px', marginRight:'500px'}}>
            <h2>Define a New Container</h2>
            {this.renderdefContainer()}

            <Link route="/form/defContainer">
            <Button content="Define new Container" icon="add circle" primary floated="left"></Button></Link>
            <br></br>
        </div>
        <br></br>
        <div style={{marginLeft:'80px', marginRight:'500px'}}>
            <h2>Transaction Record for Container</h2>
            {this.renderContainer()}

            <Link route="/form/cf">
            <Button content="Create More Transaction" icon="add circle" primary floated="left"></Button></Link>
            <br></br>
        </div>
        <br></br>
        <div style={{marginLeft:'80px', marginRight:'500px'}}>
            <h2>Transaction Record for Warehouse</h2>
            {this.renderWarehouse()}

            <Link route="/form/warehouseForm">
            <Button content="Create More Transaction" icon="add circle" primary floated="left"></Button></Link>
            <br></br>
        </div>
        </body>
        </Layout>
        </html>
        );
    }

}
export default ReviewIndex;
