import React, { Component } from 'react';
import { Button, Table } from 'semantic-ui-react';
import container from '../ethereum/container';
import web3 from '../ethereum/web3';

class RequestCont extends Component{

    onApprove = async () => {

        const accounts = await web3.eth.getAccounts();
        await container.methods.approveContainer(this.props.id).send({from: accounts[0]});

    };

    render(){
        const {Row, Cell} = Table;
        const {index, data, id} = this.props;
        console.log(data);
        for (var d of data)
        return(
            <Row>
                <Cell>{id}</Cell>
                <Cell>{data.cID}</Cell>
                <Cell>{data.HalStatus}</Cell>
                <Cell>{data.Booked}</Cell>
                <Cell>{data.SDate}</Cell>
                <Cell>{data.EDate}</Cell>
                <Cell>{data.Product}</Cell>
                <Cell>{data.Quantity}</Cell>
            </Row>
        )
    }
}

export default RequestCont;
